INSERT INTO idea VALUES(31,'2015-10-06 20:28:30.30',5,786802,'Soportes de hamacas Pyas.','Mi propuesta consiste en que en las plazas de Asunción la junta municipal ponga soportes para hamacas Paraguayas y que los Asuncenos podamos acudir a disfrutar de los espacios públicos de una manera bien tradicional, y podamos compartir como buenos paraguayos y afirmarnos en nuestras costumbres, obviamente acompañado de un rico terere.',29,34,8,8,0,2,0,0);
